const { API } = require("../../config")

export const getAllProducts = () => {
    return fetch(`${API}/getallproducts`)
    .then(res=>res.json())
    .catch(err=>console.log(err))
}

export const addProduct = (product, token) => {
    return fetch (`${API} `)
}
