import { Button, Card, CardActionArea, CardActions, CardContent, CardMedia, Grid, Link, Typography } from "@mui/material";
import { Box } from "@mui/system";
import React, { useEffect, useState } from "react";
import { getAllProducts } from "../../api/productAPI";
import CategoriesCheckbox from "../../CategoriesCheckbox";
import PriceRadio from "../../PriceRadio";
import Footer from "../Footer";
import Navbar from "../Navbar";

const Products = () => {
  const[products, setProducts] = useState([])

  useEffect(()=> {
    getAllProducts()
    .then(data=> {
      if (data.error){
        console.log(data.error)
      }
      else (setProducts(data))
    })
  }, [])
  return (
    <>
      <Navbar />
      <Box display={"flex"}>
        <Box width="25%" backgroundColor="skyblue" padding="25px">
          <Typography variant="h4" color="secondary" fontWeight="bold">
            Deals
          </Typography>

          <ul className="list-unstyled h4">
            <li>
              <Link href="#" sx={{ textDecoration: "none", color: "gold" }}>
                Deals of the day
              </Link>
            </li>
            <li>
              <Link href="#" sx={{ textDecoration: "none", color: "gold" }}>
                Flash Sales
              </Link>
            </li>
            <li>
              <Link href="#" sx={{ textDecoration: "none", color: "gold" }}>
                New year offer
              </Link>
            </li>
          </ul>

          {/* Categories */}
          <CategoriesCheckbox />

          {/* For Prices*/}
          <PriceRadio />
        </Box>
        <Box width="75%">
          <Grid container spacin={4} padding={4}>
            {
              products && products.map((product, i)=>{
                return 
              })
            }

          </Grid>
        </Box>
      </Box>
      <Footer />
    </>
  );
};

export default Products;
