import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { API } from '../../../config'
import { productDetails } from '../../api/productAPI'
import Footer from '../Footer'
import Navbar from '../Navbar'

const ProductDetails = () => {
    let [product, setProduct] = useState({})
    let{id} = useParams()

    useEffect(()=> {
        productDetails(id)
        .then(data=> {
            if(data.error){
                console.log(data)
            }
            else {
                setProduct(data)
                console.log(data)
            }
        })
    }, [])
  return (
    <>
    <Navbar/>

    <div className = "conainer mx-auto p-5">
        <div className = "row border rounded-3 shadow-lg">
            <div className = 'col-md-6 p-5'>
                <img src={`${API}/${product.product_image}`} className = 'w-100'/>
            </div>
            <div className = 'col-md-6'>
                <h3>{product.product_name}</h3>
                <h4>Rs. {product.product_price}</h4>
                <p>{product.product_description}</p>
                <h5>{product.count_in_stock}</h5>
                <h6>{product.product_price}</h6>




            </div>

        </div>
    </div>

    <Footer/>
    </>
  )
}

export default ProductDetails
