import React, { useEffect, useState } from 'react'
import { getUserList } from '../../../api/userAPI'
import Navbar from '../../Navbar'
import AdminSidebar from './AdminSidebar'

const Users = () => {
    const [users, setUsers] = useState([])

    useEffect(()=> {
        getUserList
        .then(data=> {
            if(data.error) {
                console.log(data.error)
            }
            else setUsers(data)
        })

    }, [])

    const handleRole = id = e => {
        e.preventDefault()
        changeRole(id)
        .then(data => {
            if(data.error) {
                console.log
            }
        })
    }


  return (
    <>
    <Navbar/>
   <div className="container-fluid">
    <div className="row">
      <div className="col-md-3 col-sm-6">
      <AdminSidebar/>
      </div>
      <div className="col-md-9 col-sm-6">
        <h3 className = 'text-success text-decoration-underline pt-3'>Users List</h3>
        <table className = 'table table-hover text-center'>
        <thead>
            <tr>
                <td>S.No.</td>
                <td>Username</td>
                <td>Email</td>
                <td>Verified</td>
                <td>Action</td>
            </tr>
        </thead>
        <tbody>
            {
            users && users.map((user, i)=> {
                return <tr key = {user._id}>
                    <td>{i+1}</td>
                    <td>{user.username}</td>
                    <td>{user.email}</td>
                    <td>{user.role?"admin":"client"}</td>
                    <td>{user.isVerified?"Verified": "Unverified"}</td>
                    <td>
                        <button className = "btn btn info">Make Admin</button>
                    </td>

                </tr>
            })
}
        </tbody>
        </table>
      </div>

    </div>
   </div>
    </>
  )
}

export default Users
