import { ADD_TO_CART, EMPTY_CART, REMOVE_FROM_CART } from "./cartConstants";

const initial_data = {
    cart_items: []
}

export const cartReducer = (state = initial_data, action)=> {
    switch(action.type){
        case ADD_TO_CART:
            let item = action.payload
            let itemExists = state.cart_items.find(cart_item => cart_item._id===item._id)
            if(itemExists){
                Swal.fire({
                    "title": "Warning",
                    "text": "Item already in cart",
                    "icon": "Warning"
                })
            }
            break

        case REMOVE_FROM_CART:
            break
        case EMPTY_CART:
            break

        default:
            return state
    }
}
