    ...orderItems,
    user: user._id,
    ...shipping_info
  }
  placeOrder(order)
  .then(data => {
    if (data.error){
      Swal.fire('error',data.error, 'error')
    }
    else {
        //remove items from cart
        localStorage.removeItem('cart_items')
        document.getElementById('payBtn').disabled = false
      Swal.fire('success','Your order has been placed successfully', 'success')
      .then
