import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllCategoires } from "../../../api/categoryAPI";
import Navbar from "../../Navbar";
import AdminSidebar from "./AdminSidebar";

const AdminCategories = () => {
  const [categories, setCategories] = useState([]);
  useEffect(() => {
    getAllCategorie()
      .then((data) => {
        if (data.error) {
          console.log(data.error);
        } else {
          setCategories(data);
        }
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <>
      <Navbar />
      <div className="container-fluid">
        <div className="row">
          <div className="col-md-3 col-sm-6">
            <AdminSidebar />
          </div>
          <div className="col-md-9 col-sm-6">
            <h3 className="mt-3 text-success text-decoration-underline">
              Categories
            </h3>
            <table
              className="table w-75 table-bordered text-center table-hover align-middle"
              role="button"
            >
              <thead>
                <tr className="table-dark">
                  <td>S.No.</td>
                  <td>Category Name</td>
                  <td>Action</td>
                </tr>
              </thead>
              <tbody>
                {categories &&
                  categories.map((category, index) => {
                    return (
                      <tr key={category._id}>
                        <td>{index + 1}</td>
                        <td>{category.category_name}</td>
                        <td>
                          <div className="btn-group">
                            <button className="btn btn-warning">
                              <i className="bi bi-pencil">Update</i>
                            </button>
                            <button className="btn btn-danger">
                              Remove<i className="bi bi-trash"></i>
                            </button>
                          </div>
                        </td>
                      </tr>
                    );
                  })}
                  <tr>
                    <td colspan = {3}><Link to = {'/admin/category/add'} className = "btn btn-success">Add New Category</Link></td>
                  </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </>
  );
};

export default AdminCategories;
