import { productDetails } from "../api/productAPI";
import ProductDetails from "../Layout/Pages/ProductDetails";
import { ADD_TO_CART, REMOVE_FROM_CART, EMPTY_CART } from "./cartConstants";

export const addItemToCart = (product_id, quantity) =>async (dispatch, getState) => {
    // productDetails(product_id)
    // .then(data=> {
    //     if(data.error) {
    //         console.log(data.error)
    //     }
    //     else {

    //     }
    // })
    // .catch(error) => console.log(error)
    try {
        let data = await productDetails(product_id)
        dispatch({
            type: ADD_TO_CART,
            payload: {
                ...data,
                quantity: quantity
            }
        })
    }
    catch (error){
        console.log(error)
    }

}
